function getMinecraftTime(i){i=parseInt(i);var e=i>=0&&i<13700;return{servertime:i,days:parseInt((i+8e3)/24e3),hours:(parseInt(i/1e3)+6)%24,minutes:parseInt(i/1e3%1*60),seconds:parseInt(i/1e3%1*60%1*60),day:e,night:!e}}function motd(){var i=$.getJSON("http://mcapi.ca/query/canisminor.cc/motd",function(i){return i.motd});return i}$(function(){var i=$("#server"),e="";$.getJSON("http://map.canisminor.cc/up/world/world/",function(n){var s=getMinecraftTime(n.servertime),r=s.hours+":"+s.minutes+":"+s.seconds;e+="<div class='w-card'><div class='w-avatar'><img src='//canisminor.cc/img/logo.png'></div><div class='w-box'><div class='w-title'>Server:<a href='//canisminor.cc'>Canisminor Craft</a></div><div class='w-time'><span>"+n.currentcount+"/20</span>"+r+"</div></div>",e+="<div class='w-message'>"+motd()+"</div>",e+=n?"<div class='auto-build'>Online</div>":"<div class='auto-build offline'>Offline</div>",e+="</div>",i.html(e)})});